import React, {useState} from "react";


export default function Formulario(props: any){

    let [inputContent, setInputContent] = useState(String)

    return(

        <form 
        onSubmit={(e) => {
            e.preventDefault()
            props.setNames((prevValue: string[]) => [...prevValue, inputContent ])
        }
        }
        >

            <input onChange={(e) => setInputContent(e.target.value)}/>
            <button type='submit'>Adicionar</button>
        </form>
    )
}
